//imports

import groovy.json.JsonSlurper

//grab the response and request
def response = messageExchange.response.responseContent
def request=context.expand('${${Step #4: Update a created comment}#request}')

// replace "(starting&ending), [](starting&ending) and \ with space from response
def TrimResponse =response.replaceAll('^\"|\"$','').replaceAll('^\\[|\\]$','')replaceAll('\\\\','')

//define a JsonSlurper
def jsonSlurper = new JsonSlurper()
def textResponse = jsonSlurper.parseText(TrimResponse).body.content[0].content[0].text
def textRequest = jsonSlurper.parseText(request).body.content[0].content[0].text

assert textResponse == textRequest

